import React from 'react';
import { ReactComponent as DownArrow } from '../../assets/icons/download.svg';
import Particles from "./components/Particles";
import './Header.css';

function scrollPastHeader(){
    const aboutSection = document.querySelector('.about-me')
    aboutSection.scrollIntoView({ behavior: 'smooth', block: 'start' })
}

function Header(props) {
    return (
        <div className="desktop-header">
            {/* <div className="header-my-name">
                <p style={{ fontFamily: 'Playball, cursive' }} className="animate__animated animate__bounceInLeft">
                    Andra Vraciu
                </p>
                <hr className="header-hr" />
                <p style={{ fontFamily: 'Playball, cursive' }} className="animate__animated animate__bounceInRight">
                    Front End Web Developer
                </p>
            </div>

            <div className="header-see-work animate__animated animate__bounceInRight" onClick={scrollPastHeader}>
                <p className="header-click-scroll" style={{ fontFamily: 'Playball, cursive' }}>Scroll down or click here to see some of my work</p>
                <DownArrow className="down-arrow" />
            </div> */}
            <Particles/>
        </div>
    );
};

export default Header;
