import React from 'react';
import './Project1.css';
import project1Photo from '../../assets/images/1.PNG';

function displayInfo(e){
    let hiddenInfo = document.getElementsByClassName('hidden-info');
    Array.from(hiddenInfo).forEach(el => el.style.visibility = "visible");
}

function hideInfo(e){
    let hiddenInfo = document.getElementsByClassName('hidden-info');
    Array.from(hiddenInfo).forEach(el => el.style.visibility = "hidden");
}

function displayExtended(e){
    let extendedInfo = document.getElementsByClassName('extended-info');
    Array.from(extendedInfo).forEach(el => el.style.visibility = "visible");
}

function Project1() {
    return (
        <div className="project1-container">
            <h3 style={{marginTop: '0'}}>PROJECTS</h3>
            <div>
                <p className="project-title">Visualization tool for a machine learning start-up, made in Vanilla JavaScript, HTML and CSS.</p>
                <div style={{position: 'relative', width: '50%', alignSelf: 'center'}}>
                    <img src={project1Photo} className="p1Photo" style={{width: '100%', height: 'auto'}} alt="" onMouseEnter={displayInfo} onMouseLeave={hideInfo} />
                    <button className="hidden-info" style={{ visibility: 'hidden' }} onMouseEnter={displayInfo} onMouseLeave={hideInfo} onClick={displayExtended}>Click here</button>
                    <div className="extended-info" style={{visibility: 'hidden'}}>
                        <p>more info here</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Project1;
