import React from 'react';
import { ReactComponent as GitHub } from '../../assets/icons/github.svg';
import { ReactComponent as Demo } from '../../assets/icons/global.svg';
import { ReactComponent as Check } from '../../assets/icons/check.svg';

function hideExtended3(e) {
    let extendedInfo3 = document.getElementsByClassName('extended-info3');
    Array.from(extendedInfo3).forEach(el => el.style.visibility = "hidden");
}

function ExtendedInfo1(props) {
    return (
        <div className="extended-info3 animate__animated animate__jackInTheBox" style={{ visibility: 'hidden' }}>
            <p className="close-extended1 m-4" onClick={hideExtended3}>X</p>
            <p className="mx-4">This is a simple app that uses a recipes api.</p>
            <p className="mx-4">Users can search for an ingredient or food in the designated search bar (e.g. berry, egg, carbonara) and a number of recipes will show up.</p>
            <p className="mx-4">The api did not provide the complete recipes, but instead it provided the original's recipes url, which can be accessed by clicking on the "Detailed recipe" button.</p>
            <p className="mx-4">This app, as well as the other two, can be accessed on mobile also.</p>

            <p>Technologies used:</p>
            <div className="tech-used apply-flex-column">
                <li style={{ marginTop: '10px' }}><i className="fa fa-check" aria-hidden="true"></i>React</li>
                <li><i className="fa fa-check" aria-hidden="true"></i>CSS</li>
                <li><i className="fa fa-check" aria-hidden="true"></i>Recipes api</li>
                <li><i className="fa fa-check" aria-hidden="true"></i>Unique id generator - uuid</li>
            </div>

            <div className="links-container">
                <a className="links-to-demo-code" href="https://recipes-api-app.netlify.app"><Demo className="buttons-icons" />View demo</a>
                <a className="links-to-demo-code" href="https://github.com/Andrav90/portfolio-project3"><GitHub className="buttons-icons" />View code</a>
            </div>
        </div>
    );
};

export default ExtendedInfo1;
