import React from 'react';
import { ReactComponent as GitHub } from '../../assets/icons/github.svg';
import { ReactComponent as Demo } from '../../assets/icons/global.svg';
import { ReactComponent as Check } from '../../assets/icons/check.svg';

function hideExtended2(e) {
    let extendedInfo2 = document.getElementsByClassName('extended-info2');
    Array.from(extendedInfo2).forEach(el => el.style.visibility = "hidden");
}

function ExtendedInfo1(props) {
    return (
        <div className="extended-info2 animate__animated animate__jackInTheBox" style={{ visibility: 'hidden' }}>
            <p className="close-extended1 m-4" onClick={hideExtended2}>X</p>
            <p className="mx-4">This clothing shop was made as a final project for a React course being held at Telecom Academy, Bucharest, following a JavaScript Fundamentals course.</p>
            <p className="mx-4">In this project, the entire products state is stored inside Redux, as well as the user info, coming from the login implemented with the help of Firebase Auth.</p>
            <p className="mx-4">The shop is pretty realistic, users are able to access multiple categories, filter the products by price, as well as adding or removing products from their cart or the favorites section. In order to be able to see the cart and the favorites page, one must be logged-in.</p>

            <p>Technologies used:</p>
            <ul className="apply-flex-row" style={{ justifyContent: 'space-around' }}>
                <div className="tech-used apply-flex-column">
                    <li><Check className="courses-icons" />React</li>
                    <li><Check className="courses-icons" />React Router</li>
                    <li><Check className="courses-icons" />CSS</li>
                    <li><Check className="courses-icons" />Redux</li>
                </div>
                <div className="tech-used apply-flex-column">
                    <li><Check className="courses-icons" />Redux-thunk</li>
                    <li><Check className="courses-icons" />Firebase Auth</li>
                    <li><Check className="courses-icons" />Bootstrap</li>
                </div>

            </ul>

            <div className="links-container">
                <a className="links-to-demo-code" href="https://womens-fashion-shop.netlify.app/"><Demo className="buttons-icons" />View demo</a>
                <a className="links-to-demo-code" href="https://github.com/Andrav90/telacad-project2.git"><GitHub className="buttons-icons" />View code</a>
            </div>
        </div>
    );
};

export default ExtendedInfo1;
