import React from 'react';
import { ReactComponent as GitHub } from '../../assets/icons/github.svg';
import { ReactComponent as Demo } from '../../assets/icons/global.svg';
import { ReactComponent as Check } from '../../assets/icons/checkmark.svg';
import { ReactComponent as X } from '../../assets/icons/x-mark.svg';

function hideExtended1() {
    let extendedInfo1 = document.getElementsByClassName('extended-info1');
    Array.from(extendedInfo1).forEach(el => el.style.visibility = "hidden");
}

function ExtendedInfo1() {
    return (
        // <div className="extended-info1 animate__animated animate__jackInTheBox" style={{ visibility: 'hidden' }}>
        //     <X className="close-extended" onClick={hideExtended1} />
        //     <p className="about-title">About</p>
        //     <p>This was my first complex project. It is a web-based visualization tool for a machine learning start-up.</p>
        //     <p>Their main objective at the time was to enhance low resolution images as much as possible through a machine learning process, the next step being
        //                 doing the same thing for videos.</p>
        //     <p>Thus, in this presentation website we can use different comparison tools in order to see the quality difference between the original pictures - low resolution,
        //                 and the machine learning enhanced pictures - high resolution.</p>
        //     <p>Due to the app not being online on the start-up's domain yet, I had to delete some info, like the company's name or technical data about the modified pictures.</p>

        //     <p className="technologies-title" style={{ marginTop: '30px' }}>Technologies used</p>
        //     <ul className="apply-flex-row" style={{ justifyContent: 'space-around' }}>
        //         <div className="apply-flex-column">
        //             <li><Check className="technologies-icons"/>Vanilla JavaScript</li>
        //             <li><Check className="technologies-icons"/>CSS</li>
        //         </div>
        //         <div className="apply-flex-column">
        //             <li><Check className="technologies-icons"/>HTML</li>
        //             <li><Check className="technologies-icons"/>Drift Zoom Library</li>
        //         </div>
        //     </ul>

        //     <div className="links-container apply-flex-column">
        //         <p className="links-to-demo-title">Check it out</p>
        //         <br/>
        //         <a className="links-to-demo-code" href="http://rocomandari.ro/"><Demo className="buttons-icons"/>View demo</a>
        //         <br/>
        //         <a className="links-to-demo-code" href="https://github.com/Andrav90/portfolio-project1"><GitHub className="buttons-icons"/>View code</a>
        //     </div>
        // </div>

        <div className="extended-info1 animate__animated animate__jackInTheBox" style={{ visibility: 'hidden' }}>
            <X className="close-extended" onClick={hideExtended1} />
            <p className="about-title">About</p>
            <p>This clothing shop was made as a final project for a React course being held at Telecom Academy, Bucharest, following a JavaScript Fundamentals course.</p>
            <p>In this project, the entire products state is stored inside Redux, as well as the user info, coming from the login implemented with the help of Firebase Auth.</p>
            <p>The shop is pretty realistic, users are able to access multiple categories, filter the products by price, as well as adding or removing products from their cart or the favorites section. In order to be able to see the cart and the favorites page, one must be logged-in.</p>

            <p className="technologies-title" style={{ marginTop: '30px' }}>Technologies used</p>
            <ul className="apply-flex-row" style={{ justifyContent: 'space-around' }}>
                <div className="apply-flex-column">
                    <li><Check className="technologies-icons" />React</li>
                    <li><Check className="technologies-icons" />React Router</li>
                    <li><Check className="technologies-icons" />Redux</li>
                    <li><Check className="technologies-icons" />Redux-thunk</li>
                </div>
                <div className="apply-flex-column">
                    <li><Check className="technologies-icons" />Firebase Auth</li>
                    <li><Check className="technologies-icons" />Bootstrap</li>
                    <li><Check className="technologies-icons" />CSS</li>
                    <li><Check className="technologies-icons" />JSX</li>
                </div>
                <div className="links-container apply-flex-column">
                    <p className="links-to-demo-title">Check it out</p>
                    <br />
                    <a className="links-to-demo-code" href="https://womens-fashion-shop.netlify.app/"><Demo className="buttons-icons" />View demo</a>
                    <br />
                    <a className="links-to-demo-code" href="https://github.com/Andrav90/telacad-project2.git"><GitHub className="buttons-icons" />View source code</a>
                </div>
            </ul>
        </div>
    );
};

export default ExtendedInfo1;
