import React from 'react';
import emailjs from 'emailjs-com';
import './LetsTalk.css';

function LetsTalk() {
    function sendEmail(e) {
        e.preventDefault();
        emailjs.sendForm('mail_service', 'contact_form', e.target, 'user_hjPQvThNWiLpxLwBavpB7');         
    };

    function displaySent(){
        let msgSentCont = document.getElementsByClassName('msg-sent-container')[0];
        msgSentCont.classList.add('visible');
        // msgSentCont.addEventListener('click', () => {msgSentCont.classList.remove('visible')});
    };

    return (
        <div>
            <form className="contact-form" onSubmit={sendEmail}>
                <h5 className="form-title">Feel free to contact me and say hello!</h5>
                <input type="hidden" name="contact_number" />
                <input type="text" name="user_name" placeholder="Your name" required/>
                <input type="email" name="user_email" placeholder="E-mail address" required/>
                <textarea name="message" placeholder="Your message here..." required></textarea>
                <button type="submit" className="submit-form-btn" onClick={displaySent}>Send message</button>

                <div className="msg-sent-container hidden hvr-ripple-out">
                    <img src="" id="mail-icon" className="my-3" alt="mail"/>
                    <h5 id="msg-sent-text">Message successfully sent!</h5>
                </div>
            </form>
        </div>
    );
};

export default LetsTalk;
