import React from 'react';
import Courses from './Courses';
import Skills from './Skills';
import myPicture from '../../assets/images/unnamed.jpg';
import bulletPoint from '../../assets/images/circle.png';
import './AboutMe.css';

function AboutMe() {
    return (
        <div className="about-me apply-flex-column">
            <div className="about-me-container apply-flex-column">
                <div className="apply-flex-row" style={{height: '40%'}}>
                    <img className="myPicture" alt="" src={myPicture}/>

                    {/* <div className="hexagon">
                        <div className="hexagon-inside">
                            <div className="hexagon-image">

                            </div>
                        </div>
                    </div> */}

                    <div className="apply-flex-column">
                        <h3 className="about-me-title">HELLO!</h3>
                        <div className="apply-flex-row">
                            <img src={bulletPoint} style={{ width: '10px', height: '10px', marginLeft: '8%', marginTop: '7px'}} />
                            <p className="about-me-text">My name is <b>Andra</b>. I am 23 years old and I am passionate about <b>web development</b>.</p>
                        </div>
                        <div className="apply-flex-row">
                            <img src={bulletPoint} style={{ width: '10px', height: '10px', marginLeft: '8%', marginTop: '7px' }} />
                            <p className="about-me-text">Although I got my Bachelor's and Master's Degrees in a totally different field - Public and European Administration, I wish to pursue my programming passion.</p>
                        </div>
                        <div className="apply-flex-row">
                            <img src={bulletPoint} style={{ width: '10px', height: '10px', marginLeft: '8%', marginTop: '7px' }} />
                            <p className="about-me-text">To achieve this, in late 2019 I began my journey by taking courses and spending most of my time learning programming.</p>
                        </div>
                        <div className="apply-flex-row">
                            <img src={bulletPoint} style={{ width: '10px', height: '10px', marginLeft: '8%', marginTop: '7px' }} />
                            <p className="about-me-text">Currently I am looking for a paid internship or a junior position in Bucharest or remote, to start my web developement career, meet new people and be a part of a fun team.</p>
                        </div>
                        <div className="apply-flex-row">
                            <img src={bulletPoint} style={{ width: '10px', height: '10px', marginLeft: '8%', marginTop: '7px' }} />
                            <p className="about-me-text">While I'm still a beginner and there is still much to learn, I managed to put together a few projects. Below you can see part of my work and can learn more about me.</p>
                        </div>
                        {/* <p className="about-me-text">Although I got my Bachelor's and Master's Degrees in a totally different field - Public and European Administration, I wish to pursue my programming passion.</p> */}
                        {/* <p className="about-me-text">In order to achieve this, in late 2019 I began taking courses and spending most of my time learning programming.</p> */}
                        {/* <p className="about-me-text">While I am still a beginner and there's, of course, always room for improvement, I managed to put together a few projects that you can see below, as well as a little more about me, regarding the courses I have finished and the technologies I am able to use.</p> */}
                    </div>
                </div>

                <Courses />
                <Skills />
            </div>
        </div>
    );
};

export default AboutMe;
