import React from 'react';
import { ReactComponent as GitHub } from '../../assets/icons/github.svg';
import { ReactComponent as Demo } from '../../assets/icons/global.svg';
import { ReactComponent as Check } from '../../assets/icons/check.svg';

function ExtendedInfo1(props) {
    return (
        <div className="extended-info1 animate__animated animate__jackInTheBox" style={{ visibility: 'hidden' }}>
            <p className="close-extended1 m-4" onClick={props.hideExtended1}>X</p>
            <p className="mx-4">info 2</p>
            <p className="mx-4"></p>
            <p className="mx-4"></p>
            <p className="mx-4"></p>

            <ul className="apply-flex-row" style={{ justifyContent: 'space-around' }}>
                <div className="tech-used apply-flex-column">
                    <li style={{ marginTop: '10px' }}><i className="fa fa-check" aria-hidden="true"></i>React</li>
                    <li><i className="fa fa-check" aria-hidden="true"></i>React Router</li>
                    <li><i className="fa fa-check" aria-hidden="true"></i>CSS</li>
                    <li><i className="fa fa-check" aria-hidden="true"></i>Redux</li>
                </div>
                <div className="tech-used apply-flex-column">
                    <li><i className="fa fa-check" aria-hidden="true"></i>Redux-thunk</li>
                    <li><i className="fa fa-check" aria-hidden="true"></i>Firebase Auth</li>
                    <li><i className="fa fa-check" aria-hidden="true"></i>Bootstrap</li>
                </div>

            </ul>

            <div className="links-container">
                <a className="links-to-demo-code" href="http://rocomandari.ro/"><Demo className="buttons-icons" />View demo</a>
                <a className="links-to-demo-code" href="https://github.com/Andrav90/portfolio-project1"><GitHub className="buttons-icons" />View code</a>
            </div>
        </div>
    );
};

export default ExtendedInfo1;
