import React from 'react';
import { ReactComponent as Js } from '../../assets/icons/js.svg';
import { ReactComponent as Css3 } from '../../assets/icons/css3.svg';
import { ReactComponent as Html5 } from '../../assets/icons/html5.svg';
import { ReactComponent as Firebase } from '../../assets/icons/firebase.svg';
import { ReactComponent as ReactLogo } from '../../assets/icons/react.svg';
import { ReactComponent as ReactRouter } from '../../assets/icons/react-router.svg';
import { ReactComponent as Redux } from '../../assets/icons/redux.svg';
import { ReactComponent as Bootstrap } from '../../assets/icons/bootstrap.svg';
import { ReactComponent as DownArrow } from '../../assets/icons/down-arrow.svg';
import './AboutMe.css';

function scrollToFirstProject(e) {
    const firstProject = document.querySelector('.project1-container')
    firstProject.scrollIntoView({ behavior: 'smooth', block: 'start' })
}

function AboutMe() {
    return (
        <div className="about-me apply-flex-column">
            <div className="about-me-container">
                <div className="apply-flex-row" style={{height: '40%'}}>
                    <img className="myPicture" src="data:image/jpeg;base64," alt="my-picture"/>
                    <div className="apply-flex-column">
                        <h4 className="about-me-title">About me</h4>
                        <p className="about-me-text">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent ligula lorem, bibendum eu turpis non, tincidunt iaculis lectus. Nullam et sollicitudin ante. Nulla vel facilisis elit, a pulvinar massa. Sed aliquet urna quam, sit amet ornare odio consectetur nec. Maecenas volutpat, est mattis pulvinar malesuada, justo justo mattis risus, eget efficitur est lacus in ex. Aliquam eget neque sapien. Mauris accumsan ante nec tortor rhoncus, quis condimentum lorem auctor.</p>
                    </div>
                </div>
                
                <div style={{ height: '40%' }}>
                    <h4 className="skills-title">Skills</h4>
                    <div className="skills-containers" style={{marginBottom: '3%'}}>
                        {/* apply flex row */}
                        <div className="apply-flex-column">
                            <Js className="skills-icons" />
                            <p>JavaScript</p>
                        </div>
                        <div className="apply-flex-column">
                            {/* <Css3 className="skills-icons" style={{width: '60px', height: '60px'}} /> */}
                            <img className="skills-icons" src="data:image/png;base64,"/>
                            <p>CSS</p>
                        </div>
                        <div className="apply-flex-column">
                            <Html5 className="skills-icons" style={{ width: '60px', height: '60px' }} />
                            <p>HTML5</p>
                        </div>
                        <div className="apply-flex-column">
                            <Firebase className="skills-icons" />
                            <p>Firebase</p>
                        </div>
                    </div>
                    <div className="skills-containers">
                        <div className="apply-flex-column">
                            <ReactLogo className="skills-icons"/>
                            <p>React</p>
                        </div>
                        <div className="apply-flex-column">
                            <ReactRouter className="skills-icons" />
                            <p>React Router</p>
                        </div>
                        <div className="apply-flex-column">
                            <Redux className="skills-icons" />
                            <p>Redux</p>
                        </div>
                        <div className="apply-flex-column">
                            <Bootstrap className="skills-icons" />
                            <p>Bootstrap</p>
                        </div>
                    </div>
                </div>

                <div className="section1Scroll" onClick={scrollToFirstProject} style={{marginLeft: '50%', marginTop: '5%'}}>
                    {/* <p style={{cursor: 'pointer'}}>Click here to go to the next section</p> */}
                    <DownArrow className="down-arrow" />
                </div>

            </div>
        </div>
    );
};

export default AboutMe;
