import React from 'react';
import ExtendedInfo1 from './ExtendedInfo1';
import ExtendedInfo2 from './ExtendedInfo2';
import ExtendedInfo3 from './ExtendedInfo3';
import project1Photo from '../../assets/images/project1.PNG';
import project2Photo from '../../assets/images/project2.PNG';
import project3Photo from '../../assets/images/project3.PNG';
import './Project1.css';

function mouseOver(e){
    let hiddenInfoBtn = "hidden-info-btn" + e.target.getAttribute('class').replace(/\D/g, '');
    let hiddenInfoBtnSelector = document.getElementsByClassName(hiddenInfoBtn);
    Array.from(hiddenInfoBtnSelector).forEach(el => el.style.visibility = "visible");
    // Array.from(hiddenDivSelector).forEach(el => el.style.opacity = "1");
    // Array.from(hiddenDivSelector).forEach(el => el.style.animationPlayState = "running");
}

function mouseOut(e){
    let hiddenInfoBtn = "hidden-info-btn" + e.target.getAttribute('class').replace(/\D/g, '');
    let hiddenInfoBtnSelector = document.getElementsByClassName(hiddenInfoBtn);
    Array.from(hiddenInfoBtnSelector).forEach(el => el.style.visibility = "hidden");
}

function displayExtendedInfo(e){
    let extendedInfoDiv = "extended-info" + e.target.getAttribute('class').replace(/\D/g, '');
    let extendedInfoDivSelector = document.getElementsByClassName(extendedInfoDiv);
    Array.from(extendedInfoDivSelector).forEach(el => el.style.visibility = "visible");
    Array.from(extendedInfoDivSelector).forEach(el => el.style.opacity = "1");
    Array.from(extendedInfoDivSelector).forEach(el => el.style.animationPlayState = "running");
}

// function hideExtendedInfo(e) {
//     let extendedInfoDiv = "extended-info" + e.target.getAttribute('class').replace(/\D/g, '');
//     let extendedInfoDivSelector = document.getElementsByClassName(extendedInfoDiv);
//     Array.from(extendedInfoDivSelector).forEach(el => el.style.visibility = "hidden");
//     Array.from(extendedInfoDivSelector).forEach(el => el.style.opacity = "0");
// }


function Project1() {
    return (
        <div className="project1-container">
            <h3 className="projects-title">PROJECTS</h3>
            <div>
                {/* <p className="project1-short-description">Visualization tool for a machine learning start-up, built with Vanilla JavaScript, HTML and CSS.</p>
                <div className="preview-container">
                    <img src={project1Photo} className="p1Photo" alt="" onMouseOver={displayInfo1} onMouseOut={hideInfo1} />
                    <button className="hidden-info1" style={{ visibility: 'hidden' }} onMouseOver={displayInfo1} onMouseOut={hideInfo1} onClick={displayExtended1}>Click here</button>
                   
                    <div className="extended-info1 animate__animated animate__jackInTheBox" style={{visibility: 'hidden'}}>
                        <p className="close-extended1 m-4" onClick={hideExtended1}>X</p>
                        <p className="mx-4">This was my first complex project. It is a web-based visualization tool for a machine learning start-up.</p>
                        <p className="mx-4">Their main objective at the time was to enhance low resolution images as much as possible through a machine learning process, the next step being
                        doing the same thing for videos.</p>
                        <p className="mx-4">Thus, in this presentation website we can use different comparison tools in order to see the quality difference between the original pictures - low resolution,
                        and the machine learning enhanced pictures - high resolution.</p>
                        <p className="mx-4">Due to the app not being online on the start-up's domain yet, I had to delete some info, like the company's name or technical data about the modified pictures.</p>
                        
                        <div className="links-container">
                            <a className="links-to-demo-code" href="http://rocomandari.ro/"><Demo className="buttons-icons" />View demo</a>
                            <a className="links-to-demo-code" href="https://github.com/Andrav90/portfolio-project1"><GitHub className="buttons-icons" />View code</a>
                        </div>
                    </div>
                </div> */}
                
                {/* <div className="scroll-to-next" onClick={scrollToSecondProject}>
                    <DownArrow className="down-arrow" />
                </div> */}

                <div style={{display: 'flex', flexDirection: 'row'}}>
                    <div className="projects-containers">
                        <img 
                            src={project1Photo} 
                            className="projects-photos photo-p1" 
                            onMouseOver={mouseOver} 
                            onMouseOut={mouseOut}
                            alt="project-thumbnail"
                        />
                        <button 
                            className="hidden-info-btn1" 
                            style={{ visibility: 'hidden' }} 
                            onMouseOver={mouseOver} 
                            onMouseOut={mouseOut}
                            onClick={displayExtendedInfo}
                        >
                            Learn more
                        </button>
                    </div>

                    <div className="projects-containers">
                        <img 
                            src={project2Photo} 
                            className="projects-photos photo-p2" 
                            onMouseOver={mouseOver} 
                            onMouseOut={mouseOut}
                            alt="project-thumbnail"
                        />
                        <button 
                            className="hidden-info-btn2"
                            style={{ visibility: 'hidden' }}
                            onMouseOver={mouseOver} 
                            onMouseOut={mouseOut} 
                            onClick={displayExtendedInfo}
                        >
                            Learn more
                        </button>
                    </div>

                    <div className="projects-containers">
                        <img 
                            src={project3Photo} 
                            className="projects-photos photo-p3" 
                            onMouseOver={mouseOver} 
                            onMouseOut={mouseOut}
                            alt="project-thumbnail"
                        />
                        <button 
                            className="hidden-info-btn3" 
                            style={{ visibility: 'hidden' }} 
                            onMouseOver={mouseOver} 
                            onMouseOut={mouseOut} 
                            onClick={displayExtendedInfo}
                        >
                            Learn more
                        </button>
                    </div>

                    <ExtendedInfo1
                    // hideExtendedInfo={hideExtendedInfo} 
                    />
                    <ExtendedInfo2
                    // hideExtendedInfo={hideExtendedInfo} 
                    />
                    <ExtendedInfo3
                    // hideExtendedInfo={hideExtendedInfo} 
                    />
                </div>
            </div>
        </div>
    );
};

export default Project1;
