import React from 'react';
import { ReactComponent as GitHub } from '../../assets/icons/github.svg';
import { ReactComponent as Demo } from '../../assets/icons/global.svg';
import { ReactComponent as Check } from '../../assets/icons/check.svg';

function hideExtendedInfo(e) {
    let extendedInfoDiv = "extended-info" + e.target.getAttribute('class').replace(/\D/g, '');
    let extendedInfoDivSelector = document.getElementsByClassName(extendedInfoDiv);
    Array.from(extendedInfoDivSelector).forEach(el => el.style.visibility = "hidden");
    Array.from(extendedInfoDivSelector).forEach(el => el.style.opacity = "0");
}

function hideExtended1(e) {
    let extendedInfo1 = document.getElementsByClassName('extended-info1');
    Array.from(extendedInfo1).forEach(el => el.style.visibility = "hidden");
}

function ExtendedInfo1(props) {
    return (
        <div className="extended-info1 animate__animated animate__jackInTheBox" style={{ visibility: 'hidden' }}>
            <p className="close-extended1 m-4" onClick={hideExtended1}>X</p>
            <p className="mx-4">This was my first complex project. It is a web-based visualization tool for a machine learning start-up.</p>
            <p className="mx-4">Their main objective at the time was to enhance low resolution images as much as possible through a machine learning process, the next step being
                        doing the same thing for videos.</p>
            <p className="mx-4">Thus, in this presentation website we can use different comparison tools in order to see the quality difference between the original pictures - low resolution,
                        and the machine learning enhanced pictures - high resolution.</p>
            <p className="mx-4">Due to the app not being online on the start-up's domain yet, I had to delete some info, like the company's name or technical data about the modified pictures.</p>

            <div className="tech-used apply-flex-column">
                <div className="apply-flex-row">
                    <li><Check className="courses-icons"/>Vanilla JavaScript</li>
                    <li><Check className="courses-icons"/>CSS</li>
                </div>
                <div className="apply-flex-row">
                    <li><Check className="courses-icons" />HTML</li>
                    <li><Check className="courses-icons" />Drift Zoom Library</li>
                </div>
            </div>

            <div className="links-container">
                <a className="links-to-demo-code" href="http://rocomandari.ro/"><Demo className="buttons-icons" />View demo</a>
                <a className="links-to-demo-code" href="https://github.com/Andrav90/portfolio-project1"><GitHub className="buttons-icons" />View code</a>
            </div>
        </div>
    );
};

export default ExtendedInfo1;
