import React from 'react';
import CV from '../Andra_Vraciu_CV.pdf';
// import { ReactComponent as Mail } from '../../assets/icons/mail.svg';
import { ReactComponent as GitHub } from '../../assets/icons/github.svg';
import { ReactComponent as LinkedIn } from '../../assets/icons/linkedin2.svg';
import './ContactMe.css';

// function animateContactMeIcons(e) {
//     // let contactMeIcons = "contact-me-links" + e.target.getAttribute('class').replace(/\D/g, '');
//     let contactMeIcons = document.getElementsByClassName("contact-me-links1");
//     // Array.from(extendedInfoDivSelector).forEach(el => el.style.visibility = "visible");
//     // Array.from(extendedInfoDivSelector).forEach(el => el.style.opacity = "1");
//     Array.from(contactMeIcons).forEach(el => el.style.animationPlayState = "running");
// }

function ContactMe() {
    return (
        // <div className="contact-me">
            <div className="contact-me-container">
                {/* <h3 className="contact-me-title">CONTACT ME</h3>
                <div className="contact-me-links apply-flex-row" style={{justifyContent: 'flex-around'}}>
                    <a className="contact-me-links" href="mailto:ada.vraciu@gmail.com"><Mail className="contact-me-icons animate__animated animate__shakeY"/></a>
                    <a className="contact-me-links1" href="https://github.com/Andrav90"><GitHub className="contact-me-icons animate__animated animate__shakeY"/></a>
                    <a className="contact-me-links" href="https://www.linkedin.com/in/andra-v-0b9427165/"><LinkedIn className="contact-me-icons animate__animated animate__shakeY"/></a>
                </div> */}

                <div className="apply-flex-row" style={{justifyContent: 'space-around', marginTop: '5%'}}>
                    <div className="apply-flex-column">
                        <h3 className="contact-me-title">CONTACT INFO</h3>
                        <p><strong>Phone: </strong>0730.869.754</p>
                        <p><strong>Email: </strong>ada.vraciu@gmail.com</p>
                        <a  href={CV}>
                        <p style={{ color: 'black' }}>Click here for my <strong>Curriculum Vitae</strong></p>
                        </a>
                    </div>
                    <div className="apply-flex-column">
                        <h3 className="contact-me-title">SOCIAL NETWORKS</h3>
                        <a className="contact-me-links" href="https://github.com/Andrav90">
                            <GitHub className="contact-me-icons animate__animated animate__shakeY" />
                            <p>andrav90</p>
                        </a>
                        <a className="contact-me-links" href="https://www.linkedin.com/in/andra-v-0b9427165/">
                            <LinkedIn className="contact-me-icons animate__animated animate__shakeY" />
                            <p>andravraciu</p>
                        </a>
                    </div>

                </div>
            </div>
        // </div>
    );
};

export default ContactMe;
