import React from 'react';
import './Project1.css';
import project1Photo from '../../assets/images/project1.PNG';
import project2Photo from '../../assets/images/project2.PNG';
import project3Photo from '../../assets/images/project3.PNG';
import { ReactComponent as GitHub } from '../../assets/icons/github.svg';
import { ReactComponent as Demo } from '../../assets/icons/global.svg';
import { ReactComponent as DownArrow } from '../../assets/icons/down-arrow.svg';

function displayInfo1(e){
    let hiddenInfo1 = document.getElementsByClassName('hidden-info1');
    Array.from(hiddenInfo1).forEach(el => el.style.visibility = "visible");
}

function hideInfo1(e){
    let hiddenInfo1 = document.getElementsByClassName('hidden-info1');
    Array.from(hiddenInfo1).forEach(el => el.style.visibility = "hidden");
}

function displayExtended1(e){
    let extendedInfo1 = document.getElementsByClassName('extended-info1');
    Array.from(extendedInfo1).forEach(el => el.style.visibility = "visible");
    Array.from(extendedInfo1).forEach(el => el.style.animationPlayState = "running"); 
}

function hideExtended1(e){
    let extendedInfo1 = document.getElementsByClassName('extended-info1');
    Array.from(extendedInfo1).forEach(el => el.style.visibility = "hidden");
}

// function scrollToSecondProject() {
//     const secondProject = document.querySelector('.project2-container')
//     secondProject.scrollIntoView({ behavior: 'smooth', block: 'start' })
// }


function mouseOver(e){
    let hiddenDivName = "hidden-div" + e.target.getAttribute('class').replace(/\D/g, '');
    let hiddenDivSelector = document.getElementsByClassName(hiddenDivName);
    Array.from(hiddenDivSelector).forEach(el => el.style.visibility = "visible");
    Array.from(hiddenDivSelector).forEach(el => el.style.opacity = "1");
    // Array.from(hiddenDivSelector).forEach(el => el.style.webkitAnimation = 'bounce 1s'); 
    // Array.from(hiddenDivSelector).forEach(el => el.style.animation = 'bounce 1s'); 
    Array.from(hiddenDivSelector).forEach(el => el.style.animationPlayState = "running");
    // Array.from(hiddenDivSelector).forEach(el => el.classList.add = 'animate__bounceInLeft');
    // Array.from(hiddenDivSelector).forEach(el => el.style.transition = "opacity 2s"); 
}

function Project1() {
    return (
        <div className="project1-container">
            <h3>PROJECTS</h3>
            <div>
                {/* <p className="project1-short-description">Visualization tool for a machine learning start-up, built with Vanilla JavaScript, HTML and CSS.</p>
                <div className="preview-container">
                    <img src={project1Photo} className="p1Photo" alt="" onMouseEnter={displayInfo1} onMouseLeave={hideInfo1} />
                    <button className="hidden-info1" style={{ visibility: 'hidden' }} onMouseEnter={displayInfo1} onMouseLeave={hideInfo1} onClick={displayExtended1}>Click here</button>
                   
                    <div className="extended-info1 animate__animated animate__jackInTheBox" style={{visibility: 'hidden'}}>
                        <p className="close-extended1 m-4" onClick={hideExtended1}>X</p>
                        <p className="mx-4">This was my first complex project. It is a web-based visualization tool for a machine learning start-up.</p>
                        <p className="mx-4">Their main objective at the time was to enhance low resolution images as much as possible through a machine learning process, the next step being
                        doing the same thing for videos.</p>
                        <p className="mx-4">Thus, in this presentation website we can use different comparison tools in order to see the quality difference between the original pictures - low resolution,
                        and the machine learning enhanced pictures - high resolution.</p>
                        <p className="mx-4">Due to the app not being online on the start-up's domain yet, I had to delete some info, like the company's name or technical data about the modified pictures.</p>
                        
                        <div className="links-container">
                            <a className="links-to-demo-code" href="http://rocomandari.ro/"><Demo className="buttons-icons" />View demo</a>
                            <a className="links-to-demo-code" href="https://github.com/Andrav90/portfolio-project1"><GitHub className="buttons-icons" />View code</a>
                        </div>
                    </div>
                </div> */}
                
                {/* <div className="scroll-to-next" onClick={scrollToSecondProject}>
                    <DownArrow className="down-arrow" />
                </div> */}

                <div style={{position: 'relative', width: '100%'}}>
                    <img src={project1Photo} className="projects-photos p1Photo" onMouseEnter={displayInfo1} onMouseLeave={hideInfo1}/>
                    <button className="hidden-info1" style={{ visibility: 'hidden' }} onMouseEnter={displayInfo1} onMouseLeave={hideInfo1} onClick={displayExtended1}>Learn more</button>
                </div>

                <div className="extended-info1 animate__animated animate__jackInTheBox" style={{ visibility: 'hidden' }}>
                    <p className="close-extended1 m-4" onClick={hideExtended1}>X</p>
                    <p className="mx-4">This was my first complex project. It is a web-based visualization tool for a machine learning start-up.</p>
                    <p className="mx-4">Their main objective at the time was to enhance low resolution images as much as possible through a machine learning process, the next step being
                        doing the same thing for videos.</p>
                    <p className="mx-4">Thus, in this presentation website we can use different comparison tools in order to see the quality difference between the original pictures - low resolution,
                        and the machine learning enhanced pictures - high resolution.</p>
                    <p className="mx-4">Due to the app not being online on the start-up's domain yet, I had to delete some info, like the company's name or technical data about the modified pictures.</p>

                    <div className="links-container">
                        <a className="links-to-demo-code" href="http://rocomandari.ro/"><Demo className="buttons-icons" />View demo</a>
                        <a className="links-to-demo-code" href="https://github.com/Andrav90/portfolio-project1"><GitHub className="buttons-icons" />View code</a>
                    </div>
                </div>

                <div>
                    <img src={project2Photo} className="projects-photos" onMouseEnter={displayInfo1}/>
                    <button className="hidden-info2" style={{ visibility: 'hidden' }} onClick={displayExtended1}>Learn more</button>
                </div>

                <div>
                    <img src={project3Photo} className="projects-photos" />
                    <button className="hidden-info3" style={{ visibility: 'hidden' }} onMouseEnter={displayInfo1} onMouseLeave={hideInfo1} onClick={displayExtended1}>Learn more</button>
                </div>



            </div>
        </div>
    );
};

export default Project1;
