import React from 'react';
import { SMTPClient } from 'emailjs';
import emailjs from 'emailjs-com';
import './LetsTalk.css';

function LetsTalk() {
    (function(){
        emailjs.init("user_hjPQvThNWiLpxLwBavpB7");
    })();

    let msgSentCont= document.getElementById('msg-sent-container');

    window.onload = function() {
        document.getElementById('contact-form').addEventListener('submit', function(event) {
            event.preventDefault();
            this.contact_number.value = Math.random() * 100000 | 0;
            emailjs.sendForm('contact_service', 'contact_form', this);
            msgSentCont.classList.add('visible');
        });
        msgSentCont.addEventListener('click', () => {msgSentCont.classList.remove('visible')});
    };

    return (
        <div>
            <form id="contact-form" class="apply-flex-column">
                <h5 id="form-title">Feel free to contact me and say hello!</h5>
                <input type="hidden" name="contact_number"/>
                <input type="text" name="user_name" placeholder="Your name" required/>
                <input type="email" name="user_email" placeholder="E-mail address" required/>
                <textarea name="message" placeholder="Your message here..." required></textarea>
                <button type="submit" id="submit-form-btn">Send message</button>
                <div id="msg-sent-container" class="hidden hvr-ripple-out">
                    <img src="assets/icons/mail.svg" id="mail-icon" class="my-3" alt="mail"/>
                    <h5 id="msg-sent-text">Message successfully sent!</h5>
                </div>
            </form>
        </div>
    );
};

export default LetsTalk;
