import React from 'react';
import Courses from './Courses';
import Skills from './Skills';
import myPicture from '../../assets/images/unnamed (1).jpg';
import './AboutMe.css';

function AboutMe() {
    return (
        <div className="about-me apply-flex-column">
            <div className="about-me-container apply-flex-column">
                <div className="apply-flex-row" style={{height: '40%'}}>
                    <img className="myPicture" alt="" src={myPicture}/>
                    <div className="apply-flex-column">
                        <h3 className="about-me-title">HELLO!</h3>
                        <p className="about-me-text">My name is Andra. I am 23 years old and I am passionate about front end web development.</p>
                        <p className="about-me-text">Although I got my Bachelor's and Master's Degrees in a totally different field - Public and European Administration, I wish to pursue my programming passion.</p>
                        <p className="about-me-text">In order to achieve this, in late 2019 I began taking courses and spending most of my time learning programming.</p>
                        <p className="about-me-text">While I am still a beginner and there's, of course, always room for improvement, I managed to put together a few projects that you can see below, as well as a little more about me, regarding the courses I have finished and the technologies I am able to use.</p>
                    </div>
                </div>

                <Courses />
                <Skills />
            </div>
        </div>
    );
};

export default AboutMe;
