        // </div>

        <React.Fragment>
            <Particles></Particles>
            <div className="header-my-name">
                 <p style={{ fontFamily: 'Playball, cursive' }}>
                     Andra Vraciu
                 </p>
                 <hr className="header-hr" />
                 <p style={{ fontFamily: 'Playball, cursive' }}>
                     Front End Web Developer
                 </p>
            </div>

            <span className="header-see-work" onClick={scrollToAbout}>
                <p className="header-click-scroll" style={{ fontFamily: 'Playball, cursive' }}>Scroll down or click here to see some of my work</p>
                <DownArrow className="down-arrow" />
            </span>
        </React.Fragment>

        // <div className="container" style={{position: 'relative', width: '100%', height: '100%'}}>
