import React from 'react';
import './Project1.css';
import project1Photo from '../../assets/images/1.PNG';

function displayInfo(e){
    let hiddenInfo = document.getElementsByClassName('hidden-info')
    // hiddenInfo.style.visibility = 'visible';
    Array.from(hiddenInfo).forEach(el => el.style.visibility = "visible")
}

function Project1() {
    return (
        <div className="project1-container">
            <h3 style={{marginTop: '0'}}>PROJECTS</h3>
            <div>
                <p className="project-title">Visualization tool for a machine learning start-up, made in Vanilla JavaScript, HTML and CSS.</p>
                <img src={project1Photo} className="p1Photo" onMouseEnter={displayInfo} />
                <button className="hidden-info" style={{visibility: 'hidden'}}>Click here</button>
            </div>
        </div>
    );
};

export default Project1;
